mapper.select:
select o.id, o.add_time from litemall_order o left join litemall_user u on o.user_id = u.id left join litemall_order_goods og on o.id = og.order_id
${query}
group by o.id
order by ${orderByClause}

select o.id, o.order_sn, o.order_status, o.actual_price, o.freight_price, o.add_time, o.message, o.consignee, o.address, o.mobile, o.pay_time, o.order_price, o.ship_channel, o.ship_sn, u.id user_id, u.nickname user_name, u.avatar user_avatar, o.integral_price, og.id ogid, og.goods_id, og.product_id, og.goods_name, og.pic_url goods_picture, og.specifications goods_specifications, og.number goods_number, og.price goods_price, o.root_order_id, o.parent_order_id, o.trader_id, o.trader_name, og.serial, og.bound_serial, og.max_clients_count,og.max_register_users_count, og.has_register_user_ids, og.exp_date, o.pay_type, o.pay_voucher_url, o.invoice_url, CONCAT(u.nickname, '(', u.username, ')')as buyer from litemall_order o left join litemall_user u on o.user_id = u.id left join litemall_order_goods og on o.id = og.order_id left join litemall_goods g on og.goods_id = g.id
${query}
order by ${orderByClause}
